    setErrorPopup(true);
  }
  
  return(props.trigger) ? (
    <div className='popup'>
      <div className='popupInner'>
        {props.children}
        <button className='closeButton' onClick={handleClose}>Close</button>
      </div>
    </div>
  ) :"";
}

export default Popup
